import React, {useRef} from "react";

function ShoppingListForm(): JSX.Element {

    const inputRef = useRef<HTMLInputElement>(null);

    function handleSubmit(e: React.FormEvent) {
        e.preventDefault()
        console.log("ShoppingListForm ::: submit ::: querySelector " + document.querySelector('input')!.value);
        console.log("ShoppingListForm ::: submit ::: useRef ::: " + inputRef.current!.value);

    }

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" placeholder="Product Name" ref={inputRef} />
            <button type="submit">Add Item</button>
        </form>
    )
}

export default ShoppingListForm;
